schemas:
DEFINE TABLE org SCHEMALESS;

DEFINE FIELD name ON org TYPE string;

DEFINE INDEX uniqueOrgNameIndex ON TABLE org COLUMNS name UNIQUE;

DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE store SCHEMAFULL;

DEFINE FIELD nickname ON store TYPE string;
DEFINE FIELD config ON store TYPE object;
DEFINE FIELD public ON store TYPE bool;
DEFINE FIELD org ON store TYPE record<org>;

DEFINE INDEX uniqueStoreNameIndex ON TABLE store COLUMNS nickname UNIQUE;

DEFINE TABLE token SCHEMAFULL;

DEFINE FIELD nickname ON token TYPE string;
DEFINE FIELD secret ON token TYPE string;
DEFINE FIELD perms ON token TYPE array<object>;
DEFINE FIELD owner ON token TYPE record<user>;
DEFINE FIELD org ON token TYPE record<org>;

DEFINE INDEX uniqueTokenSecretIndex ON TABLE token COLUMNS secret UNIQUE;

DEFINE TABLE user SCHEMALESS;

DEFINE FIELD name ON user TYPE string;
DEFINE FIELD org ON user TYPE record<org>;
